import React, { useState, useCallback, useRef, useReducer } from "react";
import produce from "immer";
import { getRoles } from "@testing-library/react";

// Set the number of rows and columns.  Make this adjustable later

const numRows = 10;
const numCols = 10;

// list of operations to check every neighbor
const operations = [
  [0, 1],
  [1, 1],
  [1, 0],
  [0, -1],
  [-1, -1],
  [-1, 0],
  [1, -1],
  [-1, 1],
];

const clearGrid = () => {
  const rows = [];
  for (var i = 0; i < numRows; i++) {
    rows.push(Array.from(Array(numCols), () => 0));
  }
  console.log(rows);
  return rows;
};

function Grid() {
  // Creating a grid and grid state and looping over it setting all initial values to 0

  const [running, setRunning] = useState(false);
  const runningRef = useRef(running);
  runningRef.current = running;
  const [generation, setGeneration] = useState(0);

  function initialState() {
    const rows = [];
    for (let i = 0; i < numRows; i++) {
      rows.push(Array.from(Array(numCols), () => 0));
    }
    return rows;
  }

  const [grid, setGrid] = useState(initialState);

  const runSim = useCallback(() => {
    if (!runningRef.current) {
      return;
    }
    setGrid((g) => {
      let validGrid = false;
      validGrid = false;
      return produce(g, (gridCopy) => {
        // g is og, we have have the copy, which we change, and the copy makes changes to our og, 'g'
        for (let i = 0; i < numRows; i++) {
          for (let k = 0; k < numCols; k++) {
            let neighbors = 0;
            operations.forEach(([x, y]) => {
              //looking for live/dead neighbors in every direction
              const newI = i + x;
              const newK = k + y;
              if (newI >= 0 && newI < numRows && newK >= 0 && newK < numCols) {
                neighbors += g[newI][newK];
              }
            });
            if (neighbors < 2 || neighbors > 3) {
              gridCopy[i][k] = 0;
            } else if (g[i][k] === 0 && neighbors === 3) {
              validGrid = true;
              gridCopy[i][k] = 1;
            }
          }
        }
        if (validGrid) {
          setGeneration((num) => num + 1);
        }
        setSum(
          gridCopy.flat().reduce((acc, cv) => {
            return acc + cv;
          })
        );
      });
    });
    setTimeout(runSim, timeRef.current);
  }, [timeRef.current]);
  console.log(timeRef.current);
  return (
    <div>
      <button
        onClick={() => {
          setRunning(!running);
          if (!running) {
            runningRef.current = true;
            runSimulation();
          }
        }}
      >
        {running ? "Stop" : "Start"}
      </button>
      <button
        onClick={() => {
          setGrid(clearGrid());
        }}
      >
        Clear
      </button>
      <button
        onClick={() => {
          const rows = [];
          for (var i = 0; i < numRows; i++) {
            rows.push(
              Array.from(Array(numCols), () => (Math.random() > 0.7 ? 1 : 0))
            );
          }
          setGrid(rows);
        }}
      >
        Random
      </button>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${numCols}, 20px)`,
        }}
      >
        {grid.map((rows, i) =>
          rows.map((col, j) => (
            <div
              key={`${i}-${j}`}
              //Make grid clickable
              onClick={() => {
                //Setting grid to new copy so that it is mutable
                const newGrid = produce(grid, (gridCopy) => {
                  gridCopy[i][j] = grid[i][j] ? 0 : 1;
                });
                setGrid(newGrid);
              }}
              //Setting width, height, and background color.  Make this editable later
              style={{
                width: 14,
                height: 15,
                backgroundColor: grid[i][j] ? "blue" : undefined,
                border: "1px solid black",
              }}
            />
          ))
        )}
      </div>

      <div>
        <p>Generation: {generation}</p>{" "}
      </div>
    </div>
  );
}

export default Grid;
