import React, { useState, useCallback, useRef, useReducer } from "react";
import produce from "immer";
import { getRoles } from "@testing-library/react";

// Set the number of rows and columns.  Make this adjustable later

const numRows = 10;
const numCols = 10;

const operations = [
  [0, 1],
  [1, 1],
  [1, 0],
  [0, -1],
  [-1, -1],
  [-1, 0],
  [1, -1],
  [-1, 1],
];

const clearGrid = () => {
  const rows = [];
  generationVariable = 0;
  for (var i = 0; i < numRows; i++) {
    rows.push(Array.from(Array(numCols), () => 0));
  }
  console.log(rows);
  return rows;
};

const Grid = () => {
  // Creating a grid and grid state and looping over it setting all initial values to 0
  const [grid, setGrid] = useState(() => {
    return clearGrid();
  });

  const [running, setRunning] = useState(false);
  const runningRef = useRef(running);
  runningRef.current = running;
  // let [generation, setGeneration] = useState(0);
  // let generationRef = useRef(generation);
  // generationRef = generation;

  const operations = [
    [0, 1],
    [1, 1],
    [1, 0],
    [0, -1],
    [-1, -1],
    [-1, 0],
    [1, -1],
    [-1, 1],
  ];

  // Simulation function
  const runSimulation = useCallback(() => {
    if (!runningRef.current) {
      return;
    }

    setGrid((g) => {
      return produce(g, (gridCopy) => {
        for (let i = 0; i < numRows; i++) {
          for (let j = 0; j < numCols; j++) {
            let neighbors = 0;
            operations.forEach(([x, y]) => {
              const newI = i + x;
              const newJ = j + y;
              if (newI >= 0 && newI < numRows && newJ >= 0 < numCols) {
                neighbors += g[newI][newJ];
              }
            });

            if (neighbors < 2 || neighbors > 3) {
              gridCopy[i][j] = 0;
            } else if (g[i][j] === 0 && neighbors === 3) {
              gridCopy[i][j] = 1;
            }
          }
        }
        // counter++;
        // console.log(counter);
        // generation++;
        // console.log(generation);
        generationVariable = generationVariable += 1;
        console.log(generationVariable);
        // setGeneration = generationRef.current + 1;
        // console.log(generation);
      });
    });

    setTimeout(runSimulation, 100);
  }, []);

  var count = 0;
  const generationCount = () => {
    count += 1;
  };

  var counter = setInterval(generationCount, 100);

  return (
    <div>
      <button
        onClick={() => {
          setRunning(!running);
          if (!running) {
            runningRef.current = true;
            runSimulation();
          }
        }}
      >
        {running ? "Stop" : "Start"}
      </button>
      <button
        onClick={() => {
          setGrid(clearGrid());
        }}
      >
        Clear
      </button>
      <button
        onClick={() => {
          const rows = [];
          for (var i = 0; i < numRows; i++) {
            rows.push(
              Array.from(Array(numCols), () => (Math.random() > 0.7 ? 1 : 0))
            );
          }
          setGrid(rows);
        }}
      >
        Random
      </button>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: `repeat(${numCols}, 20px)`,
        }}
      >
        {grid.map((rows, i) =>
          rows.map((col, j) => (
            <div
              key={`${i}-${j}`}
              //Make grid clickable
              onClick={() => {
                //Setting grid to new copy so that it is mutable
                const newGrid = produce(grid, (gridCopy) => {
                  gridCopy[i][j] = grid[i][j] ? 0 : 1;
                });
                setGrid(newGrid);
              }}
              //Setting width, height, and background color.  Make this editable later
              style={{
                width: 20,
                height: 20,
                backgroundColor: grid[i][j] ? "blue" : undefined,
                border: "1px solid black",
              }}
            />
          ))
        )}
      </div>

      <div>Generation: {counter} </div>
    </div>
  );
};

export default Grid;
